mapper.delete:
delete from sy_pgm_info
where comp_cd = #{compCd,jdbcType=VARCHAR}
  and pgm_id = #{pgmId,jdbcType=VARCHAR}

mapper.select:
select comp_cd, pgm_id, pgm_nm, pgm_desc, pgm_path, use_yn, bigo
from sy_pgm_info
where comp_cd = #{compCd,jdbcType=VARCHAR}
  and pgm_id = #{pgmId,jdbcType=VARCHAR}
